mapper.insert:
insert into mem(
		mem_no, mem_email, 
		mem_pw, mem_nick, 
		mem_birth
	)
values(
		#{memNo}, #{memEmail},
		#{memPw}, #{memNick},
		#{memBirth}
	)

mapper.update:
update mem set 
	mem_login_date = sysdate 
where mem_no = #{memNo}

update mem set
	mem_pw = #{memPw}
where mem_no = #{memNo}

update mem set 
	mem_nick =#{memNick}, 
	mem_intro =#{memIntro} 
where mem_no = #{memNo}
